import React, { createContext, useState, useEffect } from "react";
import axios from "./axiosConfig";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const clearError = (componentName) => {
    setErrors((prevErrors) => {
      const updatedErrors = { ...prevErrors };
      delete updatedErrors[componentName];
      return updatedErrors;
    });
  };

  const ogin = async (username, password, componentName) => {
    try {
      clearError(componentName); // Clear error state
      const response = await axios.post("/auth/login", {
        username,
        password,
      });
      const userData = { token: response.data.token, username };
      setUser(userData);
      localStorage.setItem("user", JSON.stringify(userData));
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setErrors((prevErrors) => ({
          ...prevErrors,
          [componentName]: error.response.data.error,
        }));
      } else {
        setErrors((prevErrors) => ({
          ...prevErrors,
          [componentName]: "Invalid username or password",
        }));
      }
      console.error("Login failed:", error);
    }
  };

  const signup = async (username, password, email, componentName) => {
    try {
      clearError(componentName); // Clear error state
      const response = await axios.post("/auth/signup", {
        username,
        password,
        email,
      });
      const userData = { token: response.data.token, username };
      setUser(userData);
      localStorage.setItem("user", JSON.stringify(userData));
    } catch (error) {
      if (error.response && error.response.data && error.response.data.error) {
        setErrors((prevErrors) => ({
          ...prevErrors,
          [componentName]: error.response.data.error,
        }));
      } else {
        setErrors((prevErrors) => ({
          ...prevErrors,
          [componentName]: "Signup failed",
        }));
      }
      console.error("Signup failed:", error);
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  return (
    <AuthContext.Provider value={{ user, login, signup, logout, errors }}>
      {children}
    </AuthContext.Provider>
  );
};
