import React, { useContext, useState, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import { Link } from "react-router-dom";
import styles from "./Header.module.css";

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    logout();
  };

  useEffect(() => {
    return () => {
      setDropdownOpen(false);
    };
  }, []);

  return (
    <div>
      <nav className={styles.nav}>
        <a href="/">
          <img src="/assets/amazon_logo.png" width="100" alt="Amazon Logo" />
        </a>
        <div className={styles.navCountry}>
          <img
            src="/assets/location_icon.png"
            height="20"
            alt="Location Icon"
          />
          <div className={styles.navText}>
            <p>Deliver to</p>
            <h1>United Kingdom</h1>
          </div>
        </div>
        <div
          className={styles.navText}
          onClick={() => setDropdownOpen(!dropdownOpen)}
        >
          {user ? (
            <div>
              <p>Hello, {user.username}</p>
              <h1>
                Account & Lists{" "}
                <img
                  src="/assets/dropdown_icon.png"
                  width="8px"
                  alt="Dropdown Icon"
                />
              </h1>
            </div>
          ) : (
            <p>
              Hello,{" "}
              <Link to="/login" className={styles.link}>
                Sign In
              </Link>
            </p>
          )}
          {dropdownOpen && (
            <div className={styles.dropdownContainer}>
              <ul className={styles.dropdown}>
                <li>
                  <Link to="/orders">Your Orders</Link>
                </li>
                <li>
                  <Link to="/wishlist">Your Wish List</Link>
                </li>
                <li onClick={handleLogout} className={styles.logout}>
                  Logout
                </li>
              </ul>
            </div>
          )}
        </div>
        {/* Other navigation content */}
      </nav>
      {/* Other content */}
    </div>
  );
};

export default Header;
